import axios from "axios";

const RootPath = "http://localhost:3004";

const Get = (path) => {
    const promise = new Promise((resolve, reject) => {
        axios.get(`${RootPath}/${path}`)
            .then((response) => {
                resolve(response.data);
            }, (err) => {
                reject(err);
            })
    })
}

const getNewsBlog = Get(`posts?_sort=id&_order=desc`)

const API = {
    getNewsBlog
}

export default API;
