import React, {Component, Fragment} from 'react';
import "./Product.css";

class Product extends Component {
    state = {
        order: 4,
        name: "Dimas"
    }

    handPlus = () => {
        this.setState({
            order: newValue
        })
    }

    handMinus = () => {
        alert("hahasa")
    }

    render(){
        return(
            <Fragment>
                <div className={"header"}>
                    <div className={"logo"}>
                        <img src="https://i.ytimg.com/vi/sBws8MSXN7A/hq720.jpg?sqp=-oaymwEcCNAFEJQDSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLDRsKPmNLIViU-e9sdsdHwPlyqCGQ" alt=""/>
                    </div>
                    <div className={"troley"}></div>
                    <div className={"count"}>{this.state.order}</div>
                </div>
                <div className={"card"}>
                    <div className={"img-thumb-prod"}>
                        <img src="https://i.ytimg.com/vi/sBws8MSXN7A/hq720.jpg?sqp=-oaymwEcCNAFEJQDSFXyq4qpAw4IARUAAIhCGAFwAcABBg==&rs=AOn4CLDRsKPmNLIViU-e9sdsdHwPlyqCGQ" alt=""/>
                    </div>
                    <p className={"product-title"}>Kue Balok Anada</p>
                    <p className={"product-price"}>Rp. 34.000</p>
                    <div className={"counter"}>
                        <button className={"minus"} onClick={this.handMinus}>-</button>
                        <input type="text" value={this.state.order}/>
                        <button className={"plus"} onClick={this.handPlus}>+</button>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default Product
