import Get from "./Get";
import Post from "./Post";

// POST
const postNewBlog = (data) => Post("posts", false, data);

// GET
const getNewsBlog = () => Get("posts?_sort=id&_order=desc", false)
const getComments = () => Get("comments", true)

const API = {
    postNewBlog,
    getNewsBlog,
    getComments,
}

export default API;
